import React, {useState,useEffect} from "react";
import './App.css';
import Axios from 'axios'


function App() {

  const [startUpName,setStartUpName]= useState('');
  const [startUpReview,setStartUpReview]= useState('');
  const [startUpReviewList,setStartUpList] = useState([]);

  const[newReview, setNewReview]=useState("")

  useEffect(() => {
    Axios.get("http://localhost:3001/api/get").then((response)=>{
      setStartUpList(response.data);
    })
  }, [])

  const submitReview =()=>{

    Axios.post("http://localhost:3001/api/insert",{
      startUpName:startUpName,
      startUpReview:startUpReview,
    });

      setStartUpList([
        ...setStartUpList, 
        {startUpName:startUpName,startUpReview:startUpReview},
      ]);

  };


  const deleteReview =(startup) => {
    Axios.delete('http://localhost:3001/api/delete/${startup}');
  };

  const updateReview =(startup) => {
    Axios.put("http://localhost:3001/api/update", {
      startUpName:startup,
      startUpReview:newReview,

    });
    setNewReview("")
  };


  return (
    <div className="App">
      <h1>STARTUP REVIEW APPLICATION</h1>

      <div className="form">
      <label > STARTUP NAME: </label>
      <input type="text" name="startUPName" onChange={(e)=>{
        setStartUpName(e.target.value);
      }}

       />
      <label > REVIEW: </label>
      <input type="text" name="review" onChange={(e)=>{
        setStartUpReview(e.target.value);
      }} />
      <button onClick={submitReview}>Submit</button>
      {startUpReviewList.map((val)=>{
        return (
          <div className="card">
            <h1>{val.startUpName}</h1>
            <p>{val.startUpReview}</p>
            <button onClick={() => {deleteReview(val.startUpName)}}>Delete</button>
            <input type="text" id="updateInput" onChange={(e)=>{
              setNewReview(e.target.value)
            }}/>
            <button onClick={()=>{updateReview(val.startUpName)}}>Update</button>
            </div>
        );
      })}
      </div>
    </div>
  );
}

export default App;
