import React, {useState,useEffect} from "react";
import './App.css';
import Axios from 'axios'


function App() {

  const [startUpName,setStartUpName]= useState('');
  const [startUpReview,setStartUpReview]= useState('');

  const submitReview =()=>{
    Axios.post("http://localhost:3000/api/insert",{
      startUpName:startUpName,
      startUpReview:startUpReview,
    }).then(()=>{
      alert("successful insert");
    });
  };
  return (
    <div className="App">
      <h1>STARTUP REVIEW APPLICATION</h1>

      <div className="form">
      <label > STARTUP NAME: </label>
      <input type="text" name="startUPName" onChange={()=>{
        setStartUpName(e.target.value);
      }}

       />
      <label > REVIEW: </label>
      <input type="text" name="review" onChange={()=>{
        setStartUpReview(e.target.value);
      }} />
      <button onClick={submitReview}>Submit</button>
      </div>
    </div>
  );
}

export default App;
